mapper.select:
select osm_m_index as "m_index", osm_m_id as "m_id", osm_m_name as "m_name", osm_m_nickname as "m_nickname", osm_m_birth as "m_birth", osm_m_age as "m_age", osm_m_zipcode as "m_zipcode", osm_m_address1 as "m_address1", osm_m_address2 as "m_address2", osm_m_phone as "m_phone", osm_m_email as "m_email", osm_m_date as "m_date", decode(osm_m_gender,'0','남자','여자') "gender", decode(osm_m_grant,'a','운영자','z','일반회원','미정') as "grant" from OSM_MEMBER
osm_m_id = #{memberDTO.m_id}
${searchDTO.target} = #{searchDTO.value}

mapper.update:
update OSM_MEMBER set 


		osm_m_password = #{m_password}


		osm_m_grant = #{m_grant}


		osm_m_nickname = #{m_nickname},
		osm_m_zipcode = #{m_zipcode},
		osm_m_address1 = #{m_address1},
		osm_m_address2 = #{m_address2},
		osm_m_phone = #{m_phone},
		osm_m_email = #{m_email}



	osm_m_id = #{m_id}
